INSERT INTO `intellectuelles` (`id`, `titel`, `description`, `image`) VALUES
(3, 'عروض فنية وتنشيطية للأطفال بدوار هيشر', '<p>استقبلت المكتبة العمومية بدوار هيشر عدد كبير من الأطفال والأولياء في اطارمشروع الاسرة اوالكتاب في دورته الثانية التي تنظمه الوحدة الآولى لتنشيط الاحياء بمنوبة بالشراكة مع المكتبة العمومية بدوار هيشر ايام 25 &ndash; 26 و 27 ديسمبر 2019 ويحتوي المشروع على جملة من الأنشطة الترفيهية والتكوينية ( عروض راقصة , مسرح , عرض حكواتي , ورشات في صنع الأقنعة &hellip;.) ويهدف الى الترغيب في المطالعة وحث الاولياء على تشجيع ابنائهم على ارتياد المكتبات والتصالح مع الكتاب في ظل الزحف التكنولوجي .</p>\r\n', 'DSC01272-750x430.jpg'),
(4, 'بعث نادي الشطرنج بدار الشباب', '<p>بعث نادي الشطرنج بدار الشباب الرقاب بإشراف الأستاذ نجيب بالطيّب، ويهدف النادي إلى نشر وتعميم ممارسة لعبة الشطرنج لما لها من فائدة في تنمية القدرات الذهنيّة والفكريّة لدى الشباب.. كما يهدف إلى تطوير المهارات الخاصّة بهذه اللّعبة التي تجد إشعاعا عالميّا اعتبارا لتاريخيّتها ودورها في تنشيط الذّكاء الفردي والجماعي.. إضافة إلى ذلك، فإنّ من أهداف النّادي المشاركة في البطولات التي تهمّ رياضة الشطرنج جهويّا ووطنيّا ودوليّا.</p>\r\n\r\n<p>الدّعوة مفتوحة للشّباب المولع بهذه اللّعبة للالتحاق بالنادي صباح كلّ أحد انطلاقا من الساعة التّاسعة صباحا.</p>\r\n', '02.jpg');

